import React from 'react';
import { CartState } from '../App';
import TopCard from './Cards Components/topCard';
import './Components Styles/Menu.css';
import Filter from './Filter';
// import { user } from '../App';


const Menu = () => {
  // var {cart,setCart,cartSize, setCartSize, discount, setDiscount, totalPrice, setTotalPrice} = useContext(user);

  const { state: { products, cart }, dispatch } = CartState();
  // console.log(products);

  return (
    <div id="menuContainer">
      <div id="filterContainer">
        <Filter />
      </div>
      <div id="menuItemsContainer">
        <div id="heading"> <h1> Our Products - <span>{products.length} items </span></h1> <input placeholder='search by name' id="search" /> <i class="fa-solid fa-magnifying-glass"></i> </div>
        <div id="menuItemsListContainer">
        {products.map((val) => {
          return <TopCard val={val} key={val.idMeal} />
        })}
        </div>
      </div>
    </div>
  )
}

export default Menu
