import React from 'react';
import { CartState } from '../App';
import '../Components/Components Styles/Menu.css'

const Filter = () => {

     const {productState :{ search,sortPrice,sortName},productDispatch} = CartState();
   
     return (
          <div id="filterContainer">
               <h3> Filters    <i className="fa-solid fa-filter"></i> </h3>
               <div id="radioFilter">
                    <p> Name - </p>
                    <span><input type='radio' name='group-1' value='ascending-name' onChange={()=>{
                         productDispatch({
                              type:'byName',
                              payload:"lowToHigh"

                         })
                    }} 
                    checked = {sortName ==='lowToHigh' ? 'true' : 'false'}/> <label> Ascending </label></span>
                    <span><input type='radio' name='group-1' value='descending-name' /> <label> Descending </label></span>
               </div>
               <div id="radioFilter">
                    <p> Price - </p>
                    <span>
                         <input type='radio' name='group-1' value='ascending-price' /> <label> Ascending </label>
                    </span>
                    <span>
                         <input type='radio' name='group-1' value='descending-price' /> <label> Descending </label>
                    </span>
               </div>
               <button> Clear Filters </button>
          </div>
     )
}

export default Filter
