import React from 'react';
import { CartState } from '../App';
import TopCard from './Cards Components/topCard';
import './Components Styles/Menu.css';
import Filter from './Filter';
// import { user } from '../App';


const Menu = () => {
  // var {cart,setCart,cartSize, setCartSize, discount, setDiscount, totalPrice, setTotalPrice} = useContext(user);

  const { state: { products },productState:{ search,sortPrice,sortName},productDispatch } = CartState();
  // console.log(products);

  const transformedProducts = ()=>{
     let sortedProducts = products;

     if(sortPrice)
     {
       sortedProducts = sortedProducts.sort((a,b)=>
         sortPrice === 'lowToHigh' ? a.price - b.price : b.price - a.price
       )
     }
     if(sortName)
     {
       sortedProducts = sortedProducts.sort((a,b)=>
         sortPrice === 'lowToHigh' ? a.strMeal.toLowerCase().trim() - b.strMeal.toLowerCase().trim() : b.strMeal.toLowerCase().trim() - a.strMeal.toLowerCase().trim()
       )
     }


     console.log(sortedProducts);
     return sortedProducts
  }

  return (
    <div id="menuContainer">
      <div id="filterContainer">
        <Filter />
      </div>
      <div id="menuItemsContainer">
        <div id="heading"> <h1> Our Products - <span>{products.length} items </span></h1> <input placeholder='search by name' id="search" onChange={(e)=>{
          productDispatch({
            type:'searchByName',
            payload:e.target.value
          })
        }} /></div>
        <div id="menuItemsListContainer">
        {transformedProducts().map((val) => {
          return <TopCard val={val} key={val.idMeal} />
        })}
        </div>
      </div>
    </div>
  )
}

export default Menu
