import React from 'react'
import { Link } from 'react-router-dom';
import { CartState } from '../App'
import '../Components/Components Styles/Cart.css'
import CartCard from './Cards Components/CartCard';

const Cart = () => {

  const { state: { cart } } = CartState();
  console.log(cart);
  return (
    <div id="cartContainer">
      <h1> Items in your cart - ({cart.length}) </h1>
      <div id="cartListContainer">
        {cart.length === 0 ? <div>
                         <img src=' https://chillydraji.files.wordpress.com/2015/08/empty_cart.jpeg ' alt='Empty Cart'/> 
                         <h1> Your Product cart is empty </h1>
                         <Link to="/menu"><button>Continue Shopping</button></Link>
                    </div>
                    : <CartCard />}
      </div>
    </div>
  )
}

export default Cart
