import React from 'react'
import { CartState } from '../App'
import '../Components/Components Styles/Cart.css'
import CartCard from './Cards Components/CartCard';

const Cart = () => {

  const { state: { products, cart }, dispatch } = CartState();
  console.log(cart);
  return (
    <div id="cartContainer">
      <h1> Items in your cart </h1>
      <div id="cartListContainer">
        {cart.length === 0 ?  <></>: cart.map((val,index)=>{
          return(
            <CartCard val={val} index={index} key={val.idMeal}/>
          )
        })}
      </div>
    </div>
  )
}

export default Cart
