export const cartReducer = (state, action) => {
     switch (action.type) {

          case 'addToCart':
               return { ...state, cart: [...state.cart, { ...action.payload, qty: 1 }] }

          case 'removeFromCart':
               return { ...state, cart: state.cart.filter((c) => c.idMeal !== action.payload.idMeal) }

          case "increaseQTY":
               return { ...state, cart: state.cart.filter((c) => (c.idMeal !== action.payload.idMeal) && c.qty++) }

          case "decreaseQTY":
               return { ...state, cart: state.cart.filter((c) => (c.idMeal !== action.payload.idMeal) && c.qty--) }

          default:
               return state;
     };
}

export const productReducer = (state, action) => {
     switch (action.type) {
          case 'byPrice':
               return {...state,}

          case 'byName':

               break;

          default:
               return state;
     }
}
